package ustdemo;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=5,n2=3,add,sub,pro,quo;
		add = n1+n2;
		sub=n1-n2;
		pro=n1*n2;
		quo=n1/n2;
		System.out.print("sum is "+add);
		System.out.print(" diff is "+sub);
		System.out.print(" product is "+pro);
		System.out.print(" quotient is "+quo);
		

	}

}
